mapper.select:
SELECT a.id, a.title, a.odd_number oddNum, IFNULL(a.process_instance_id, '') processInstanceId, a.state, CONVERT(a.create_time, char) createTime FROM asset_management_use a WHERE a.create_id = #{userId}
AND a.state = #{state}
AND a.create_time >= #{startTime} AND #{endTime} >= a.create_time
ORDER BY a.create_time DESC

mapper.update:
UPDATE asset_management_use

    state = '1',
    process_instance_id = #{processInId}

WHERE id = #{id}

UPDATE asset_management_use

    remark = #{remark},
    enclosure_info = #{enclosureInfo}

WHERE id = #{id}

update asset_management_use_goods

    state = #{applyState}

WHERE use_id = #{id}
AND state = '1'

UPDATE asset_management_use
state = '5', process_instance_id = ''
WHERE id = #{id}
