mapper.select:
SELECT a.id, a.title, a.odd_number oddNum, a.process_instance_id processInstanceId, a.state, CONVERT(a.create_time, char) createTime FROM licence_manage_revert a WHERE a.create_id = #{userId}
AND a.state = #{state}
AND a.create_time >= #{startTime} AND #{endTime} >= a.create_time
ORDER BY a.create_time DESC

mapper.update:
UPDATE licence_manage_revert

    remark = #{remark},
    enclosure_info = #{enclosureInfo}

WHERE id = #{id}

UPDATE licence_manage_revert

    state = '1',
    process_instance_id = #{processInId}

WHERE id = #{id}

update licence_manage_revert
state = #{state}, process_instance_id = ''
WHERE id = #{id}
