mapper.select:
SELECT a.id, a.title, a.odd_number oddNum, IFNULL(a.process_instance_id,'') processInstanceId, a.state, IF((a.state = 0 OR a.state = 1 OR a.state = 3 OR a.state = 5), 1, -1) editRow, CASE a.state WHEN '0' THEN '草稿' WHEN '1' THEN '审核中' WHEN '2' THEN '审核通过' WHEN '3' THEN '审核不通过' WHEN '4' THEN '作废' WHEN '5' THEN '撤销' END stateName, CONVERT(a.create_time, char) createTime FROM check_work_leave a WHERE a.create_id = #{userId}
AND a.state = #{state}
AND a.create_time >= #{startTime} AND #{endTime} >= a.create_time
ORDER BY a.create_time DESC

mapper.update:
UPDATE check_work_leave

    state = #{state},
    process_instance_id = #{processInId}

WHERE id = #{leaveId}

UPDATE check_work_leave

    remark = #{remark},
    enclosure_info = #{enclosureInfo}

WHERE id = #{id}
