mapper.select:
SELECT
	a.id
FROM
	sys_develop_doc_type a
WHERE a.title = #{typeName}
AND a.id != #{id}

SELECT a.id, a.title, a.content, a.state, CASE a.state WHEN 0 THEN '新建' WHEN 1 THEN '上线' WHEN 2 THEN '下线' ELSE '错误码' END stateName, CONVERT(a.create_time, char) createTime FROM sys_develop_doc a WHERE a.type_id = #{parentId}
AND a.`title` LIKE '%${title}%'
ORDER BY a.order_by ASC

SELECT
	a.id
FROM
	sys_develop_doc a
WHERE a.type_id = #{parentId}
AND a.title = #{title}

SELECT
	a.id
FROM
	sys_develop_doc a
WHERE a.type_id = #{parentId}
AND a.title = #{title}
AND a.id != #{id}
