mapper.select:
SELECT a.id, CONCAT(d.name, '-', b.title) opportunityName, c.name typeName, CONVERT(a.documentary_time, char) documentaryTime, a.create_name createName, a.detail FROM crm_documentary a LEFT JOIN crm_opportunity b ON a.opportunity_id = b.id LEFT JOIN crm_documentary_type c ON a.type_id = c.id LEFT JOIN crm_customer d ON d.id = b.customer_id WHERE a.create_id = #{userId}
AND b.title LIKE '%${opportunityName}%'
AND a.type_id = #{typeId}
AND #{endTime} >= a.documentary_time AND a.documentary_time >= #{startTime}
ORDER BY a.create_time desc

SELECT a.id, CONCAT(d.name, '-', b.title) opportunityName, c.name typeName, CONVERT(a.documentary_time, char) documentaryTime, a.create_name createName, a.detail FROM crm_documentary a LEFT JOIN crm_opportunity b ON a.opportunity_id = b.id LEFT JOIN crm_documentary_type c ON a.type_id = c.id LEFT JOIN crm_customer d ON d.id = b.customer_id WHERE 1 = 1
AND b.title LIKE '%${opportunityName}%'
AND a.type_id = #{typeId}
AND #{endTime} >= a.documentary_time AND a.documentary_time >= #{startTime}
ORDER BY a.create_time desc
