mapper.select:
SELECT a.id, a.archives_number archivesNumber, a.custody_place custodyPlace, a.archives_center archivesCenter, a.state archivesState, DATE_FORMAT(a.archives_time,'%Y-%m-%d') archivesTime, DATE_FORMAT(a.create_time, '%Y-%m-%d') createTime, a.remark, a.enclosure, a.whether_archives whetherArchives, b.user_name userName, b.job_number jobNumber, b.state, c.company_name companyName, d.`name` educationName FROM sys_staff_archives a LEFT JOIN company_mation c ON a.management_unit = c.id LEFT JOIN sys_staff_data_dictionary d ON a.education_id = d.id, sys_eve_user_staff b WHERE a.staff_id = b.id
AND b.user_name LIKE '%${userName}%'
AND b.job_number LIKE '%${jobNumber}%'
AND a.archives_number = #{archivesNumber}
AND a.state = #{archivesState}
AND b.state = #{state}
ORDER BY a.create_time DESC

SELECT a.id, a.archives_number archivesNumber, a.custody_place custodyPlace, a.archives_center archivesCenter, a.state archivesState, DATE_FORMAT(a.archives_time,'%Y-%m-%d') archivesTime, DATE_FORMAT(a.create_time, '%Y-%m-%d') createTime, a.remark, a.enclosure, a.whether_archives whetherArchives, b.user_name userName, b.job_number jobNumber, b.state, c.company_name companyName, d.`name` educationName FROM sys_staff_archives a LEFT JOIN company_mation c ON a.management_unit = c.id LEFT JOIN sys_staff_data_dictionary d ON a.education_id = d.id, sys_eve_user_staff b WHERE a.staff_id = b.id AND b.state IN(2, 5) AND a.whether_archives = 1
AND b.user_name LIKE '%${userName}%'
AND b.job_number LIKE '%${jobNumber}%'
AND a.archives_number = #{archivesNumber}
AND a.state = #{archivesState}
ORDER BY a.create_time DESC

SELECT a.id, a.archives_number archivesNumber, a.custody_place custodyPlace, a.archives_center archivesCenter, a.state archivesState, DATE_FORMAT(a.archives_time,'%Y-%m-%d') archivesTime, DATE_FORMAT(a.create_time, '%Y-%m-%d') createTime, a.remark, a.enclosure, a.whether_archives whetherArchives, b.user_name userName, b.job_number jobNumber, b.state, c.company_name companyName, d.`name` educationName FROM sys_staff_archives a LEFT JOIN company_mation c ON a.management_unit = c.id LEFT JOIN sys_staff_data_dictionary d ON a.education_id = d.id, sys_eve_user_staff b WHERE a.staff_id = b.id AND a.whether_archives = 2
AND b.user_name LIKE '%${userName}%'
AND b.job_number LIKE '%${jobNumber}%'
AND a.archives_number = #{archivesNumber}
AND a.state = #{archivesState}
ORDER BY a.create_time DESC

mapper.update:
UPDATE sys_staff_archives
archives_number = #{archivesNumber}, management_unit = #{managementUnit}, custody_place = #{custodyPlace}, archives_center = #{archivesCenter}, archives_time = #{archivesTime}, education_id = #{educationId}, whether_archives = #{whetherArchives}, state = #{archivesState}, remark = #{remark}, enclosure = #{enclosureInfo},
WHERE id = #{id}
