mapper.insert:
INSERT INTO erp_inoutitem(
    id, name, type, remark, delete_flag, create_time
) VALUES (
    #{id}, #{inoutitemName}, #{inoutitemType}, #{remark}, #{deleteFlag}, #{createTime}
)

mapper.update:
UPDATE erp_inoutitem
name = #{inoutitemName}, type = #{inoutitemType}, remark = #{remark}
WHERE id = #{id} AND delete_flag = 0
