mapper.select:
SELECT a.id, d.forum_title title, d.id forumId, CASE WHEN a.report_type_id = 'other' THEN '其他' ELSE c.type_name END as reportType, a.examine_state examineState, a.report_other_content reportContent, a.report_desc reportDesc, b.user_name reportUser, CONVERT(a.report_time, char) reportTime FROM forum_report a LEFT JOIN sys_eve_user_staff b ON a.report_id = b.user_id LEFT JOIN forum_report_type c ON a.report_type_id = c.id LEFT JOIN forum_content d ON a.forum_id = d.id WHERE a.examine_state = '1'
AND a.report_type_id = #{reportTypeId}
AND d.forum_title like '%${title}%'
AND #{reportendTime} >= a.report_time AND a.report_time >= #{reportstartTime}
ORDER BY a.report_time desc

SELECT a.id, e.forum_title title, e.id forumId, CASE WHEN a.report_type_id = 'other' THEN '其他' ELSE c.type_name END as reportType, a.examine_state examineState, a.report_other_content reportContent, a.report_desc reportDesc, a.examine_nopass_reason examineNopassReason, b.user_name reportUser, CONVERT(a.report_time, char) reportTime, d.user_name examineUser, CONVERT(a.examine_time, char) examineTime FROM forum_report a LEFT JOIN sys_eve_user_staff b ON a.report_id = b.user_id LEFT JOIN forum_report_type c ON a.report_type_id = c.id LEFT JOIN sys_eve_user_staff d ON a.examine_id = d.user_id LEFT JOIN forum_content e ON a.forum_id = e.id WHERE (a.examine_state = '2' OR a.examine_state = '3')
AND a.report_type_id = #{reportTypeId}
AND e.forum_title like '%${title}%'
AND #{reportendTime} >= a.report_time AND a.report_time >= #{reportstartTime}
AND #{examineendTime} >= a.examine_time AND a.examine_time >= #{examinestartTime}
ORDER BY a.examine_time desc
