mapper.select:
SELECT a.id, a.`name` userName, a.personal_phone phone, a.work_phone telePhone, a.email, a.company unitName, a.create_id createId, #{userId} userId, a.readonly, b.`name` typeName FROM sys_mail_list a LEFT JOIN sys_mail_type b ON a.type_id = b.id WHERE a.category = #{category}
AND a.create_id = #{userId}
AND (a.`name` LIKE '%${searchCondition}%' OR a.personal_phone LIKE '%${searchCondition}%' OR a.work_phone LIKE '%${searchCondition}%')
ORDER BY a.create_time DESC

mapper.delete:
DELETE
FROM
    sys_mail_list
WHERE
    id = #{id}
AND create_id = #{userId}

mapper.update:
UPDATE sys_mail_list
`name` = #{name},
category = #{category}, readonly = #{readonly}, type_id = #{typeId}, name_call = #{nameCall}, company = #{company}, department = #{department}, personal_phone = #{personalPhone}, work_phone = #{workPhone}, fax = #{fax}, email = #{email}, other_phone = #{otherPhone}, other_email = #{otherEmail}, work_address = #{workAddress}, work_code = #{workCode}, person_address = #{personAddress}, person_code = #{personCode}, company_url = #{companyUrl}, person_url = #{personUrl}, birthday = #{birthday}, remarks = #{remarks}
WHERE id = #{id}
