mapper.select:
SELECT a.id, a.department_name departmentName, a.department_desc departmentDesc, (SELECT COUNT(*) FROM sys_eve_user_staff b WHERE b.department_id = a.id) userNum, (SELECT COUNT(*) FROM company_job c WHERE c.department_id = a.id) jobNum, CONVERT(a.create_time, char) createTime, a.overtime_settlement_type overtimeSettlementType FROM company_department a WHERE a.company_id = #{companyId}
AND a.department_name LIKE '%${departmentName}%'
AND a.overtime_settlement_type = #{overtimeSettlementType}
ORDER BY a.create_time DESC

SELECT
	a.id,
	a.department_name departmentName
FROM
	company_department a
WHERE 
	a.company_id = #{companyId}
	AND a.department_name = #{departmentName}
	AND a.id != #{id}

mapper.update:
UPDATE company_department


		department_name = #{departmentName},

	department_desc = #{departmentDesc},
	overtime_settlement_type = #{overtimeSettlementType}

WHERE id = #{id}
