mapper.select:
SELECT a.id, a.title, b.project_name proName, a.project_number projectNumber, a.all_work_load allWorkLoad, a.state, DATE_FORMAT(a.create_time, '%Y-%m-%d') createTime, c.user_name createName, IFNULL(d.process_instance_id, '') processInstanceId, IF((a.state = 0 OR a.state = 1 OR a.state = 12 OR a.state = 3), 1, -1) editRow FROM pro_workload a LEFT JOIN pro_workload_process d ON a.id = d.workload_id, pro_project b, sys_eve_user_staff c WHERE a.create_id = #{userId} AND a.pro_id = b.id AND c.user_id = #{userId}
AND b.project_name like '%${proName}%'
AND a.title like '%${workloadName}%'
AND a.state = #{state}
ORDER BY a.create_time DESC

SELECT a.id, a.title, b.project_name projectName, a.project_number projectNumber, a.all_work_load allWorkLoad, a.state, DATE_FORMAT(a.create_time, '%Y-%m-%d') createTime, c.user_name createName, IFNULL(d.process_instance_id, '') processInstanceId, g.title taskType FROM pro_workload a LEFT JOIN pro_workload_process d ON a.id = d.workload_id, pro_project b, sys_eve_user_staff c, act_model g WHERE a.pro_id = b.id AND c.user_id = a.create_id AND g.page_url = #{pageUrl}
AND b.project_name like '%${proName}%'
AND a.title like '%${workloadName}%'
AND a.state = #{state}
ORDER BY a.create_time DESC

update.foreach:
UPDATE pro_task

	actual_workload = actual_workload + #{item.allWorkload}

WHERE id = #{item.taskId}

mapper.update:
UPDATE pro_workload
title = #{title}, pro_id = #{proId}, project_number = #{projectNumber}, `desc` = #{desc}, type = #{type}, all_work_load = #{allWorkLoad}, enclosure_info = #{enclosureInfo}
WHERE id = #{id}
