mapper.select:
SELECT a.id, a.job_number jobNumber, a.user_name userName, a.user_photo userPhoto, a.user_idcard userIdCard, a.user_sex userSex, a.email, a.phone, a.home_phone homePhone, a.qq, a.state, c.school_name schoolName FROM sys_eve_user_staff a, school_staff_mation b, school_mation c WHERE a.id = b.staff_id AND b.school_id = c.id AND a.`type` = '2'
AND a.user_name LIKE '%${userName}%'
AND a.user_idcard LIKE '%${userIdCard}%'
AND a.user_sex = #{userSex}
AND c.id = #{schoolPowerId}
AND c.id = #{schoolId}
ORDER BY a.state ASC, a.entry_time DESC

SELECT a.id staffId, IFNULL(a.user_id, '') userId, a.job_number jobNumber, a.user_name userName, a.user_photo userPhoto, CASE a.user_sex WHEN '2' THEN '女' WHEN '1' THEN '男' ELSE '保密' END userSex, a.email, a.phone, a.home_phone homePhone, a.qq, c.school_name schoolName, CASE WHEN a.user_id = null THEN '未分配帐号' WHEN LENGTH(a.user_id) > 0 THEN '已分配帐号' ELSE '未分配帐号' END userCodeState FROM sys_eve_user_staff a, school_staff_mation b, school_mation c WHERE a.id = b.staff_id AND b.school_id = c.id AND a.`type` = '2' AND a.state NOT IN(2, 5)
AND a.user_name LIKE '%${userName}%'
AND a.user_sex = #{userSex}
AND a.user_id != #{userId}
AND a.user_id IS NOT NULL AND LENGTH(a.user_id) > 0
AND c.id = #{schoolPowerId}
AND c.id = #{schoolId}
ORDER BY a.state ASC, a.entry_time DESC
