mapper.select:
SELECT a.id, a.order_num orderNum, b.`name` serviceTypeName, a.state, CASE a.state WHEN '1' THEN '待派工' WHEN '2' THEN '待接单' WHEN '3' THEN '待签到' WHEN '4' THEN '待完工' WHEN '5' THEN '待评价' WHEN '6' THEN '待审核' WHEN '7' THEN '已完工' END stateName, CONVERT(a.declaration_time, CHAR) declarationTime, a.contacts, a.phone, a.customer_name customerName, a.product_name productName, c.user_name receiver, a.type, CASE a.type WHEN '1' THEN '微信报单' ELSE '用户报单' END typeName, d.user_name declarationName FROM crm_service a LEFT JOIN sys_eve_user_staff d ON a.declaration_id = d.user_id LEFT JOIN sys_eve_user_staff c ON a.service_user_id = c.user_id, crm_service_type b WHERE a.type_id = b.id
AND a.order_num = #{orderNum}
AND a.state = #{state}
AND a.contacts LIKE '%${contacts}%'
AND c.user_name LIKE '%${receiver}%'
ORDER BY a.declaration_time desc

SELECT a.id, a.order_num orderNum, b.`name` serviceTypeName, a.customer_name customerName, a.product_name productName, a.state, '待派工' stateName, CONVERT(a.declaration_time, CHAR) declarationTime FROM crm_service a, crm_service_type b WHERE a.state = 1 AND a.type_id = b.id
AND a.order_num = #{orderNum}
AND a.type_id = #{typeId}
AND a.customer_name LIKE '%${customerName}%'
AND a.declaration_time >= #{firstTime} AND #{lastTime} >= a.declaration_time
ORDER BY a.declaration_time desc

SELECT a.id, a.order_num orderNum, b.`name` serviceTypeName, a.customer_name customerName, a.product_name productName, a.state, '待接单' stateName, CONVERT(a.declaration_time, CHAR) declarationTime FROM crm_service a, crm_service_type b WHERE a.service_user_id = #{receiver} AND a.state = 2 AND a.type_id = b.id
AND a.order_num = #{orderNum}
AND a.type_id = #{typeId}
AND a.customer_name LIKE '%${customerName}%'
AND a.declaration_time >= #{firstTime} AND #{lastTime} >= a.declaration_time
ORDER BY a.declaration_time desc

