SELECT a.id, a.order_num orderNum, #工单号 b.`name` serviceTypeName, #服务类型 a.customer_name customerName, #客户名称 a.product_name productName, #产品名称 a.state, '已完工' stateName, CONVERT(a.declaration_time, CHAR) declarationTime #报单时间 FROM crm_service a, crm_service_type b WHERE a.state = 7 AND a.type_id = b.id
AND a.order_num = #{orderNum}
AND a.type_id = #{typeId}
AND a.customer_name LIKE '%${customerName}%'
