mapper.update:
UPDATE crm_service

    service_user_id = #{serviceUserId},
    cooperation_user_id = #{cooperationUserId},
    state = 2,
    service_time = #{serviceTime}

WHERE id = #{id}

mapper.select:
SELECT a.id, a.service_id serviceId, #工单Id b.order_num orderNum, #工单号 a.apply_num applyNum, #申领单号 a.state, CASE a.state WHEN '0' THEN '待审核' WHEN '1' THEN '审核通过' WHEN '2' THEN '审核不通过' END stateName, #状态名称 IFNULL(d.`name`, b.customer_name) customerName, #客户名称 CONVERT(a.apply_time, CHAR) applyTime, #申领时间 c.user_name applyUserName #申领人 FROM crm_service_apply a LEFT JOIN crm_customer d ON d.id = a.customer_id, crm_service b, sys_eve_user_staff c WHERE a.create_id = #{receiver} AND a.service_id = b.id AND a.create_id = c.user_id
AND a.order_num = #{orderNum}
AND a.state = #{state}
AND a.apply_num = #{applyNum}
AND b.customer_name LIKE '%${customerName}%'

SELECT a.id, a.service_id serviceId, #工单Id b.order_num orderNum, #工单号 a.apply_num applyNum, #申领单号 IFNULL(f.`name`, b.customer_name) customerName, #客户名称 CONVERT(a.apply_time, CHAR) applyTime, #申领时间 c.user_name applyUserName #申领人 FROM crm_service_apply a LEFT JOIN crm_customer f ON f.id = a.customer_id, crm_service b, sys_eve_user_staff c, crm_service_apply_material d, erp_material e WHERE a.service_id = b.id AND a.create_id = c.user_id AND a.state = 1 AND d.apply_id = a.id AND d.material_id = e.id
AND a.order_num = #{orderNum}
AND a.apply_num = #{applyNum}
AND b.customer_name LIKE '%${customerName}%'
GROUP BY a.id ORDER BY a.create_time DESC

mapper.update:
UPDATE crm_service_apply
apply_time = #{applyTime}, state = #{state}, remark = #{remark}, customer_id = #{customerId}, all_price = #{allPrice}, enclosure_info = #{enclosureInfo},
WHERE id = #{id}

mapper.select:
SELECT a.id, a.service_id serviceId, #工单Id b.order_num orderNum, #工单号 a.apply_num applyNum, #申领单号 IFNULL(f.`name`, b.customer_name) customerName, #客户名称 CONVERT(a.apply_time, CHAR) applyTime, #申领时间 c.user_name applyUserName, #申领人 a.state FROM crm_service_apply a LEFT JOIN crm_customer f ON f.id = a.customer_id, crm_service b, sys_eve_user_staff c, crm_service_apply_material d, erp_material e WHERE a.service_id = b.id AND a.create_id = c.user_id AND a.state = 0 AND d.apply_id = a.id AND d.material_id = e.id
AND a.order_num = #{orderNum}
AND a.apply_num = #{applyNum}
AND b.customer_name LIKE '%${customerName}%'
GROUP BY a.id ORDER BY a.create_time DESC

update.foreach:

UPDATE crm_service_apply_material

    oper_number = #{item.operNum},
    all_price = #{item.allPrice}

WHERE id = #{item.id}

