WHERE id = #{id}

mapper.select:
SELECT a.id, a.order_num orderNum, b.`name` serviceTypeName, a.customer_name customerName, a.product_name productName, a.state, CASE a.state WHEN '1' THEN '待派工' WHEN '2' THEN '待接单' WHEN '3' THEN '待签到' WHEN '4' THEN '待完工' WHEN '5' THEN '待评价' WHEN '6' THEN '待审核' WHEN '7' THEN '已完工' END stateName, CONVERT(a.declaration_time, CHAR) declarationTime FROM crm_service a, crm_service_type b WHERE a.type_id = b.id AND a.create_id = #{createId}
AND a.order_num = #{orderNum}
AND a.type_id = #{typeId}
AND a.customer_name LIKE '%${customerName}%'
AND a.declaration_time >= #{firstTime} AND #{lastTime} >= a.declaration_time
ORDER BY a.declaration_time desc
