mapper.select:
SELECT a.id, a.user_code userCode, k.id staffId, k.job_number jobNumber, k.email email, k.user_name userName, k.user_sex sexName, a.user_lock userLock, (SELECT c.user_name FROM sys_eve_user_staff c WHERE a.creater = c.user_id) createName, (SELECT group_concat(distinct b.role_name) FROM sys_eve_role b WHERE INSTR(CONCAT(',', a.role_id, ','), CONCAT(',', b.id, ','))) roleName, m.company_name companyName, m.department_name departmentName, m.job_name jobName, CONVERT(a.create_time, char) createTime FROM sys_eve_user a LEFT JOIN (SELECT o.user_id, p.job_name, q.department_name, u.company_name FROM sys_eve_user_staff o, company_job p, company_department q, company_mation u WHERE o.company_id = u.id AND o.department_id = q.id AND o.job_id = p.id) m ON a.id = m.user_id, sys_eve_user_staff k WHERE a.id = k.user_id
AND a.user_code like '%${userCode}%'
AND k.user_name like '%${userName}%'
AND k.user_sex = #{sexName}
AND k.company_id = #{companyName}
AND k.department_id = #{departmentName}
AND k.job_id = #{jobName}
ORDER BY a.user_lock ASC, a.create_time DESC

mapper.update:
UPDATE sys_eve_user
password = #{password}, pwd_num_enc = #{pwdNum},
WHERE id = #{id}

UPDATE sys_eve_user_install

	win_bg_pic_vague = #{winBgPicVague},
	win_bg_pic_vague_value = #{winBgPicVagueValue}

WHERE user_id = #{userId}
