mapper.select:
SELECT a.id, a.job_number jobNumber, a.user_name userName, a.user_photo userPhoto, a.user_idcard userIdCard, a.user_sex userSex, a.email, a.phone, a.user_id userId, a.home_phone homePhone, a.qq, a.state, u.company_name companyName, q.department_name departmentName, p.job_name jobName, a.`type` staffType, COUNT(b.check_work_time_id) workTimeNum FROM sys_eve_user_staff a LEFT JOIN sys_eve_user_staff_time b ON a.id = b.staff_id, company_job p, company_department q, company_mation u WHERE a.company_id = u.id AND a.department_id = q.id AND a.job_id = p.id
AND a.user_name LIKE '%${userName}%'
AND a.user_idcard LIKE '%${userIdCard}%'
AND a.user_sex = #{userSex}
AND a.company_id = #{companyName}
AND a.department_id = #{departmentName}
AND a.job_id = #{jobName}
GROUP BY a.id ORDER BY a.state ASC, a.entry_time DESC

SELECT
	a.id
FROM
	sys_eve_user_staff a
WHERE a.user_idcard = #{userIdCard}
AND a.id != #{id}

mapper.update:
UPDATE sys_eve_user_staff
user_photo = #{userPhoto}, user_idcard = #{userIdCard}, email = #{email}, user_sex = #{userSex}, phone = #{phone}, home_phone = #{homePhone}, qq = #{qq}, user_sign = #{userSign}, company_id = #{companyId}, department_id = #{departmentId}, job_id = #{jobId}, job_score_id = #{jobScoreId}, work_time = #{workTime},
WHERE id = #{id}

UPDATE sys_eve_user_staff a

	a.state = '2',
	a.quit_time = #{quitTime},
	a.quit_reason = #{quitReason}

WHERE a.id = #{id}

UPDATE sys_eve_user a

	a.user_lock = 1

WHERE a.id = #{id}

mapper.select:
SELECT a.id, a.job_number jobNumber, a.user_name userName, a.user_photo userPhoto, a.user_idcard userIdCard, a.user_sex userSex, a.email, a.phone, a.home_phone homePhone, a.qq, a.state, u.company_name companyName, q.department_name departmentName, p.job_name jobName, a.`type` staffType FROM sys_eve_user_staff a, company_job p, company_department q, company_mation u WHERE a.company_id = u.id AND a.department_id = q.id AND a.job_id = p.id
AND a.user_name LIKE '%${userName}%'
AND a.user_idcard LIKE '%${userIdCard}%'
AND a.user_sex = #{userSex}
AND a.company_id = #{companyName}
AND a.department_id = #{departmentName}
AND a.job_id = #{jobName}
ORDER BY a.state ASC, a.entry_time DESC

mapper.update:
UPDATE sys_eve_user_staff

	annual_leave = #{quarterYearHour},
	annual_leave_statis_time = #{annualLeaveStatisTime}

WHERE id = #{staffId}

UPDATE sys_eve_user_staff

	holiday_number = #{holidayNumber},
	holiday_statis_time = #{holidayStatisTime}

WHERE id = #{staffId}

UPDATE sys_eve_user_staff

	retired_holiday_number = #{retiredHolidayNumber},
	retired_holiday_statis_time = #{retiredHolidayStatisTime}

WHERE id = #{staffId}
