mapper.select:
SELECT a.id, a.name_cn nameCn, a.name_en nameEn, a.state, bu.user_name createName, DATE_FORMAT(CONVERT(a.create_time, CHAR),'%Y-%m-%d') createTime, cu.user_name lastUpdateName, DATE_FORMAT(CONVERT(a.last_update_time, CHAR),'%Y-%m-%d') lastUpdateTime FROM wages_model_type a LEFT JOIN sys_eve_user_staff bu ON a.create_id = bu.user_id LEFT JOIN sys_eve_user_staff cu ON a.last_update_id = cu.user_id WHERE a.state != '3'
AND (a.name_cn LIKE '%${name}%' OR a.name_en LIKE '%${name}%')
AND a.state = #{state}

mapper.update:
UPDATE wages_model_type
name_cn = #{nameCn}, name_en = #{nameEn},
WHERE id = #{id}
