mapper.select:
SELECT a.id, a.job_number jobNumber, a.user_name userName, a.user_photo userPhoto, a.user_sex userSex, a.email, a.phone, a.home_phone homePhone, a.qq, a.state, u.company_name companyName, q.department_name departmentName, p.job_name jobName, a.`type` staffType FROM sys_eve_user_staff a, company_job p, company_department q, company_mation u WHERE a.company_id = u.id AND a.department_id = q.id AND a.job_id = p.id AND a.state NOT IN(2, 5) AND a.design_wages = 1
AND a.user_name LIKE '%${userName}%'
AND a.job_number LIKE '%${jobNumber}%'
AND a.user_sex = #{userSex}
AND a.company_id = #{companyId}
AND a.department_id = #{departmentId}
AND a.job_id = #{jobId}
ORDER BY a.entry_time DESC

update.foreach:
UPDATE wages_field_staff_mation

    amount_money = #{item.amountMoney}

WHERE staff_id = #{item.staffId}
AND field_type_key = #{item.fieldKey}

mapper.update:
UPDATE sys_eve_user_staff

    design_wages = #{state},
    act_wages = #{actMoney}

WHERE id = #{staffId}

mapper.select:
SELECT a.id, a.job_number jobNumber, a.user_name userName, a.user_photo userPhoto, a.user_sex userSex, a.email, a.phone, a.home_phone homePhone, a.qq, a.state, u.company_name companyName, q.department_name departmentName, p.job_name jobName, a.`type` staffType, CONVERT(a.act_wages, decimal(24, 2)) actWages FROM sys_eve_user_staff a, company_job p, company_department q, company_mation u WHERE a.company_id = u.id AND a.department_id = q.id AND a.job_id = p.id AND a.design_wages = 2
AND a.user_name LIKE '%${userName}%'
AND a.job_number LIKE '%${jobNumber}%'
AND a.user_sex = #{userSex}
AND a.company_id = #{companyId}
AND a.department_id = #{departmentId}
AND a.job_id = #{jobId}
ORDER BY a.entry_time DESC
